  var MONTHNAMES = [null,"January","February","March","April","May","June","July","August","September","October","November","December"];
  Event.observe(window, 'load', function() {
    [$('byYearForm'), $('byMonthForm')].each(function(form) {
      form.observe('submit', function(e) {
        Event.stop(e);
        var yearField = form.down('select[name=year]');
        var monthField = form.down('select[name=month]');
        var url = "/book/popular_by_date/";
        if (yearField != null) {
          url += yearField.value + '/';
        };
        if (monthField != null) {
          url += MONTHNAMES[monthField.value] + '/';
        };
        window.location.href = url;
        return false;
      });
    });
  });
